#!/bin/bash

install_brewfile() {
    # Install packages from Brewfile (generated by Alchemist)
    if [[ "$HOSTNAME" == "mbp-justin" ]]; then
        brew bundle --file "$HOME/.dotfiles/src/personal/Brewfile"
    elif [[ "$HOSTNAME" == "db1" ]]; then
        brew bundle --file "$HOME/.dotfiles/src/servers/db1/Brewfile"
    elif [[ "$HOSTNAME" == "db2" ]]; then
        brew bundle --file "$HOME/.dotfiles/src/servers/db2/Brewfile"
    elif [[ "$HOSTNAME" == "dev1" ]]; then
        brew bundle --file "$HOME/.dotfiles/src/servers/dev1/Brewfile"
    elif [[ "$HOSTNAME" == "lb1" ]]; then
        brew bundle --file "$HOME/.dotfiles/src/servers/lb1/Brewfile"
    elif [[ "$HOSTNAME" == "web1" ]]; then
        brew bundle --file "$HOME/.dotfiles/src/servers/web1/Brewfile"
    elif [[ "$HOSTNAME" == "web2" ]]; then
        brew bundle --file "$HOME/.dotfiles/src/servers/web2/Brewfile"
    elif [[ "$HOSTNAME" == "mbp-justin-ep" ]]; then
        brew bundle --file "$HOME/.dotfiles/src/easypost/Brewfile"
    else
        echo "HOSTNAME doesn't match any config for Brewfile installation."
    fi
}

install_brewfile
